(function($){$.fn.vslider=function(options){options=$.extend({duration:600,auto:0,easing:'swing',view:1},options);return this.each(function(){var that=$(this),next=$('#'+that.attr('id')+'-next'),prev=$('#'+that.attr('id')+'-prev'),content=$('#'+that.attr('id')+'-content'),active=true,vsToggle=function(){active=!active;},scrollNext=function(){if(active){vsToggle();content.children(':first').hide({duration:options.duration,easing:options.easing,complete:function(){$(this).detach().appendTo(content).show(0,vsToggle);}});}},scrollPrev=function(){if(active){vsToggle();content.children(':last').hide(0,function(){$(this).detach().prependTo(content).slideDown({duration:options.duration,easing:options.easing,complete:vsToggle});});}};that.css({'overflow':'hidden','height':content.children().height()*options.view});content.children(':last').detach().prependTo(content);that.css({"position":"relative"});content.css({"position":"absolute","margin-top":"-="+(content.children().height())});if(options.auto>0){window.setInterval(scrollNext,options.auto);}
next.click(function(){scrollNext();return false;});prev.click(function(){scrollPrev();return false;});});};})(jQuery);